import "./HomePage.css";
import image from "../assets/search.png";
import useState from "react";
const HomePage = () => {
  function CenterPieces(props) {
    return <div>{props.value}</div>;
  }

  const [state, setState] = useState({
    value: "",
    show: "",
  });

  const handleChange = (e) => {
    setState({ value: e.target.value });
  };

  const submit = () => {
    setState({ show: state.value });
  };

  return (
    <div>
      <div id="title">
        <div class="image">
          <CenterPieces
            value={<img className="image" src={image} alt="react"></img>}
          />
        </div>
        <div class="titleText">
          <CenterPieces value={<h1>Pocket Watcher</h1>} />
        </div>
      </div>
      <div className="block">
        <div className="bodyText">
          <h3>
            Pocket Watcher is a tool that can track total net worth based on how
            much ethereum you have and your nft worth. Checks your nft worth
            based on the floor and not by traits.
          </h3>
        </div>
      </div>
      <div className="inputBox">
        <form onSubmit={() => submit()}>
          <input
            type="text"
            value={state.value}
            onChange={(e) => handleChange(e)}
          />
          <input type="submit" />
        </form>
        <h2>{state.show}</h2>
      </div>
    </div>
  );
};

export default HomePage;
